from collections import deque
import time,os,sys



def typingPrint(text):
  for character in text:
    sys.stdout.write(character)
    sys.stdout.flush()
    time.sleep(0.05)





class QuestionNode:

    def __init__(self, question):
        self.question = question
        self.children = []

    def add_child(self, node):
        self.children.append(node)

