class TreeNode:

    def __init__(self, title, rating, genre, duration, topic):
        self.title = title
        self.rating = rating
        self.duration = duration
        self.genre = genre
        self.topic = topic
        self.children = []

    def add_child(self, child_node):
        self.children.append(child_node)
        

