def bfs(root, target1, target2, target3, target4):
    path_queue = deque()
    initial_path = [root]
    path_queue.appendleft(initial_path)
    while path_queue:
        current_path = path_queue.pop()
        current_node = current_path[-1]
        for ratings in target1:
            if ratings == current_node.rating:
                for genres in target2:
                    if genres in current_node.genre:
                        if target3 < int(current_node.duration) and int(current_node.duration) < target4:
                            typingPrint("\n\n""Here's a movie I think you might enjoy: ")
                            print("\n\n",current_node.title,":",current_node.topic)
                            like = input("\nWhat do you think? Are you happy with your recommendation, or would you like a new selection? If you'd like a new movie recommendation, type Y, otherwise type any other letter. Type Here: ")
                            if like == "Y" or like == "y":
                                continue
                            else:
                                print("Enjoy your movie! Thanks for choosing FlickPick")
                                quit()
        for child in current_node.children:
            new_path = current_path[:]
            new_path.append(child)
            path_queue.appendleft(new_path)
    print("Sorry we couldn't find a movie matching your criteria")


